import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import { FixedSizeList, VariableSizeList } from './virtualList';
const Row = ({ index, style, forwardRef }) => {
  return (
    <div className={index % 2 ? 'list-item-odd' : 'list-item-even'} style={style} ref={forwardRef}>
      {`Row ${index}`}
    </div>
  )
}
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <div className='grid'>

    </div>
    <header>高度固定的虚拟列表</header>
    <FixedSizeList
        className="list"
        height={200}
        width={200}
        itemSize={50}
        itemCount={1000}
      >
        {Row}
      </FixedSizeList>
    <header>子项高度不固定的虚拟列表</header>
    <VariableSizeList
      className="list"
      height={200}
      width={200}
      itemSize={getItemSize}
      itemCount={1000}
    >
      {Row}
    </VariableSizeList>    
  </React.StrictMode>
);
